select a.name,sum(b.value) from v$statname a , v$sesstat b where a.statistic#=b.statistic# and (a.name in ('user commits','commit cleanouts','commit cleanouts successfully completed','commit batch requested','commit immediate requested','commit batch/immediate performed') or a.name like 'commit cleanout failures%') and b.sid in (select distinct sid from v$session where username='VISHNU') and b.value <> 0 group by a.name order by 2;
